query GetBrandById($brandId: ID!, $first: Int!, $after: String) {
  brandById(input: {brandId: $brandId}) {
    id
    name
    storesV2(first: $first, after: $after, scope: MENU_READ) {
      edges {
        node {
          ...StoreFields
          __typename
        }
        __typename
      }
      pageInfo {
        ...PageInfoFields
        __typename
      }
      __typename
    }
    __typename
  }
}

fragment StoreFields on Store {
  id
  brandId
  checkPermissions(input: {scopes: [STORE_MENU_EDIT, STORE_MENU_READ]}) {
    permitted
    __typename
  }
  currencyCode
  facility {
    ...FacilityFields
    __typename
  }
  storeLinksV2(first: 1000) {
    edges {
      node {
        ...StoreLinkFields
        __typename
      }
      __typename
    }
    __typename
  }
  __typename
}

fragment FacilityFields on Facility {
  id
  name
  facilityBrandName
  address {
    ...AddressFields
    __typename
  }
  timezone
  currencyCode
  __typename
}

fragment AddressFields on Address {
  addressOne
  addressTwo
  latLong {
    latitude
    longitude
    __typename
  }
  countryCode
  __typename
}

fragment StoreLinkFields on StoreLink {
  ofoSlug
  accountConnectionsV2(first: 1000) {
    edges {
      node {
        ...AccountConnectionFields
        __typename
      }
      __typename
    }
    __typename
  }
  serviceConfiguration {
    serviceType
    __typename
  }
  __typename
}

fragment AccountConnectionFields on AccountConnection {
  accountId
  isActive
  type
  serviceSlug
  connectionStatus
  settings {
    ...AccountConnectionSettingsFields
    __typename
  }
  __typename
}

fragment AccountConnectionSettingsFields on AccountConnectionSettings {
  __typename
  ... on MenuConnectionSettings {
    canServeAlcoholAlone
    cannotPublishAlcohol
    __typename
  }
}

fragment PageInfoFields on PageInfo {
  endCursor
  hasNextPage
  hasPreviousPage
  startCursor
  __typename
}
